"use strict";(self.webpackChunkcasibase_website=self.webpackChunkcasibase_website||[]).push([[8122],{2348:e=>{e.exports=JSON.parse('{"blogPosts":[{"id":"/casibase-deepSeek-guideline","metadata":{"permalink":"/ko/blog/casibase-deepSeek-guideline","editUrl":"https://github.com/casibase/casibase-website/edit/master/i18n/ko/docusaurus-plugin-content-blog/casibase-deepSeek-guideline.md","source":"@site/i18n/ko/docusaurus-plugin-content-blog/casibase-deepSeek-guideline.md","title":"Casibase + DeepSeek \ud1b5\ud569 \uac00\uc774\ub4dc","description":"\uc624\ub298\ub0a0\uc758 \ub514\uc9c0\ud138 \uc2dc\ub300\uc5d0\uc11c \uae30\uc5c5\uacfc \uac1c\uc778\uc758 \ud6a8\uc728\uc801\uc778 \uc9c0\uc2dd \uad00\ub9ac \uc2dc\uc2a4\ud15c\uacfc \uc9c0\ub2a5\ud615 \uc9c8\uc758\uc751\ub2f5 \uc2dc\uc2a4\ud15c\uc5d0 \ub300\ud55c \uc218\uc694\uac00 \uc99d\uac00\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc778\uacf5\uc9c0\ub2a5 \uae30\uc220\uc758 \uae09\uc18d\ud55c \ubc1c\uc804\uacfc \ud568\uaed8, \uc0ac\ub78c\ub4e4\uc740 \uc815\ubcf4 \ucc98\ub9ac\ub97c \uac1c\uc120\ud558\uace0, \uc6cc\ud06c\ud50c\ub85c\uc6b0\ub97c \ucd5c\uc801\ud654\ud558\uba70, \uc0ac\uc6a9\uc790 \uacbd\ud5d8\uc744 \ud5a5\uc0c1\uc2dc\ud0a4\uae30 \uc704\ud55c \ub354 \uc2a4\ub9c8\ud2b8\ud558\uace0 \ud6a8\uc728\uc801\uc778 \uc194\ub8e8\uc158\uc744 \ucc3e\uace0 \uc788\uc2b5\ub2c8\ub2e4.","date":"2025-03-17T11:54:13.432Z","formattedDate":"2025\ub144 3\uc6d4 17\uc77c","tags":[{"label":"casibase","permalink":"/ko/blog/tags/casibase"}],"readingTime":7.225,"hasTruncateMarker":false,"authors":[{"name":"Casibase \ud300","url":"https://github.com/casibase","imageURL":"https://avatars.githubusercontent.com/casibase"}],"frontMatter":{"title":"Casibase + DeepSeek \ud1b5\ud569 \uac00\uc774\ub4dc","author":"Casibase \ud300","author_url":"https://github.com/casibase","author_image_url":"https://avatars.githubusercontent.com/casibase","tags":["casibase"]},"nextItem":{"title":"\ud658\uc601\ud569\ub2c8\ub2e4","permalink":"/ko/blog/welcome"}},"content":"\uc624\ub298\ub0a0\uc758 \ub514\uc9c0\ud138 \uc2dc\ub300\uc5d0\uc11c \uae30\uc5c5\uacfc \uac1c\uc778\uc758 \ud6a8\uc728\uc801\uc778 \uc9c0\uc2dd \uad00\ub9ac \uc2dc\uc2a4\ud15c\uacfc \uc9c0\ub2a5\ud615 \uc9c8\uc758\uc751\ub2f5 \uc2dc\uc2a4\ud15c\uc5d0 \ub300\ud55c \uc218\uc694\uac00 \uc99d\uac00\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc778\uacf5\uc9c0\ub2a5 \uae30\uc220\uc758 \uae09\uc18d\ud55c \ubc1c\uc804\uacfc \ud568\uaed8, \uc0ac\ub78c\ub4e4\uc740 \uc815\ubcf4 \ucc98\ub9ac\ub97c \uac1c\uc120\ud558\uace0, \uc6cc\ud06c\ud50c\ub85c\uc6b0\ub97c \ucd5c\uc801\ud654\ud558\uba70, \uc0ac\uc6a9\uc790 \uacbd\ud5d8\uc744 \ud5a5\uc0c1\uc2dc\ud0a4\uae30 \uc704\ud55c \ub354 \uc2a4\ub9c8\ud2b8\ud558\uace0 \ud6a8\uc728\uc801\uc778 \uc194\ub8e8\uc158\uc744 \ucc3e\uace0 \uc788\uc2b5\ub2c8\ub2e4.\\n\\nCasibase\ub294 \uac15\ub825\ud55c \uba40\ud2f0 \ubaa8\ub378 \ud638\ud658\uc131, \uc5d4\ud130\ud504\ub77c\uc774\uc988\uae09 \uae30\ub2a5 \uc9c0\uc6d0, \uc9c1\uad00\uc801\uc778 \uc6f9 \uc778\ud130\ud398\uc774\uc2a4\ub97c \uac16\ucd98 \uc624\ud508\uc18c\uc2a4 AI \uc9c0\uc2dd\ubca0\uc774\uc2a4 \ubc0f \ub300\ud654 \uc2dc\uc2a4\ud15c\uc73c\ub85c\uc11c \ub9ce\uc740 \uac1c\ubc1c\uc790\uc640 \uae30\uc5c5\uc758 \uc120\ud638\ud558\ub294 \uc194\ub8e8\uc158\uc774 \ub418\uc5c8\uc2b5\ub2c8\ub2e4. \uc9c0\uc2dd\uc744 \ud6a8\uc728\uc801\uc73c\ub85c \uc870\uc9c1\ud558\uace0 \uac80\uc0c9\ud560 \uc218 \uc788\uc744 \ubfd0\ub9cc \uc544\ub2c8\ub77c, \ub2e4\uc591\ud55c \uc2dc\ub098\ub9ac\uc624\ub97c \uc9c0\uc6d0\ud558\uae30 \uc704\ud574 \ub2e4\uc591\ud55c AI \ubaa8\ub378\uc744 \uc27d\uac8c \ud1b5\ud569\ud560 \uc218 \uc788\ub294 \uc720\uc5f0\ud55c \uc778\ud130\ud398\uc774\uc2a4\ub97c \uc81c\uacf5\ud569\ub2c8\ub2e4.\\n\\n\ub3d9\uc2dc\uc5d0, \ucd5c\uadfc \ucd9c\uc2dc\ub41c \uace0\uae09 AI \ubaa8\ub378\uc778 **DeepSeek R1**\uc740 \ub6f0\uc5b4\ub09c \uc131\ub2a5, \ucd5c\uc801\ud654\ub41c \uc99d\ub958 \uae30\uc220, \ubb34\ub8cc \uc624\ud508\uc18c\uc2a4 \ud2b9\uc131\uc73c\ub85c \uc778\ud574 \uac1c\ubc1c\uc790 \ucee4\ubba4\ub2c8\ud2f0\uc5d0\uc11c \ud070 \uc8fc\ubaa9\uc744 \ubc1b\uc558\uc2b5\ub2c8\ub2e4. DeepSeek R1\uc758 \uac15\ub825\ud55c \ucd94\ub860 \ub2a5\ub825\uacfc \ud6a8\uc728\uc801\uc778 \uc9c0\uc2dd \ud1b5\ud569 \ub2a5\ub825\uc740 \uc9c0\ub2a5\ud615 \uc9c8\uc758\uc751\ub2f5, \ucf54\ub4dc \uc0dd\uc131, \ud14d\uc2a4\ud2b8 \uc774\ud574 \ub4f1\uc758 \uc791\uc5c5\uc5d0\uc11c \ub6f0\uc5b4\ub09c \uc131\uacfc\ub97c \ubcf4\uc774\uba70 \uc624\ud508\uc18c\uc2a4 AI \uc0dd\ud0dc\uacc4\uc758 \uc911\uc694\ud55c \uad6c\uc131\uc6d0\uc774 \ub418\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uc774 \uae00\uc5d0\uc11c\ub294 Casibase\ub97c DeepSeek R1\uacfc \ud6a8\uc728\uc801\uc73c\ub85c \ud1b5\ud569\ud558\uc5ec \uace0\uc131\ub2a5, \ubcf4\uc548\uc131, \uac15\ub825\ud55c \uae30\ub2a5\uc744 \uac16\ucd98 AI \uc9c0\uc2dd \uc2dc\uc2a4\ud15c\uacfc \ub300\ud654 \uc2dc\uc2a4\ud15c\uc744 \uad6c\ucd95\ud558\ub294 \ubc29\ubc95\uc744 \ub17c\uc758\ud560 \uac83\uc785\ub2c8\ub2e4. \ud658\uacbd \uc124\uc815\ubd80\ud130 \uc2e4\uc81c \uc751\uc6a9\uae4c\uc9c0\uc758 \ud575\uc2ec \ub2e8\uacc4\ub97c \uc18c\uac1c\ud558\uace0, Casibase\uc758 \uc5d4\ud130\ud504\ub77c\uc774\uc988\uae09 \uae30\ub2a5\uacfc DeepSeek R1\uc758 \uac15\ub825\ud55c \ub2a5\ub825\uc744 \ud65c\uc6a9\ud558\uc5ec \uc815\ud655\ud558\uace0 \ud6a8\uc728\uc801\uc778 \uc9c0\ub2a5\ud615 \uc9c8\uc758\uc751\ub2f5\uacfc \uc9c0\uc2dd \uad00\ub9ac\ub97c \uc2e4\ud604\ud558\ub294 \ubc29\ubc95\uc744 \ubd84\uc11d\ud560 \uac83\uc785\ub2c8\ub2e4.\\n\\n## Casibase\ub780:\\n\\nCasibase\ub294 \ucd5c\uc2e0 RAG(Retrieval-Augmented Generation) \uae30\uc220, \uc5d4\ud130\ud504\ub77c\uc774\uc988\uae09 \uc2f1\uae00 \uc0ac\uc778\uc628(SSO), \uc8fc\ub958 AI \ubaa8\ub378\uc5d0 \ub300\ud55c \uad11\ubc94\uc704\ud55c \uc9c0\uc6d0\uc744 \uacb0\ud569\ud55c \uc624\ud508\uc18c\uc2a4 AI \uc9c0\uc2dd \uc2dc\uc2a4\ud15c \ubc0f \ub300\ud654 \uc2dc\uc2a4\ud15c\uc785\ub2c8\ub2e4. LangChain\uacfc \uc720\uc0ac\ud55c \uc2dc\uc2a4\ud15c\uc73c\ub85c\uc11c, Casibase\ub294 \uae30\uc5c5\uacfc \uac1c\ubc1c\uc790\uc5d0\uac8c \uac15\ub825\ud558\uace0 \uc720\uc5f0\ud558\uba70 \uc0ac\uc6a9\ud558\uae30 \uc26c\uc6b4 \uc9c0\uc2dd \uad00\ub9ac \ubc0f \uc9c0\ub2a5\ud615 \ub300\ud654 \ud50c\ub7ab\ud3fc\uc744 \uc81c\uacf5\ud558\ub294 \uac83\uc744 \ubaa9\ud45c\ub85c \ud569\ub2c8\ub2e4.\\n\\n\ud604\uc7ac Casibase\ub294 OpenAI \uae30\ubc18 GPT \uc2dc\ub9ac\uc988, Gemini, Claude, Moonshot, DeepSeek \ub4f1\uc758 \uc5b8\uc5b4 \ubaa8\ub378\uacfc OpenAI \uae30\ubc18 \uc784\ubca0\ub529 \ubaa8\ub378, Hugging Face\uc758 sentence-transformers, Cohere, Qwen \ub4f1\uc758 \uc784\ubca0\ub529 \ubaa8\ub378\uc744 \uc9c0\uc6d0\ud569\ub2c8\ub2e4. Hugging Face, Cohere, Qwen, and other embedding models. \uc774\ub7ec\ud55c \uad11\ubc94\uc704\ud55c \ubaa8\ub378 \uc9c0\uc6d0\uc744 \ud1b5\ud574 \uc0ac\uc6a9\uc790\ub294 \uc694\uad6c\uc0ac\ud56d\uacfc \uc120\ud638\ub3c4\uc5d0 \ub530\ub77c \uac00\uc7a5 \uc801\ud569\ud55c AI \ubaa8\ub378\uc744 \uc120\ud0dd\ud560 \uc218 \uc788\uc73c\uba70, \ud5a5\ud6c4 \uc0c8\ub85c\uc6b4 \ubaa8\ub378\uc758 \ud1b5\ud569\ub3c4 \uc6a9\uc774\ud569\ub2c8\ub2e4.\\n\\n### Casibase \uc628\ub77c\uc778 \ub370\ubaa8:\\n\\nCasibase\ub294 \uc0ac\uc6a9\uc790\uac00 \uc2dc\uc2a4\ud15c \uae30\ub2a5\uc744 \uc9c1\uad00\uc801\uc73c\ub85c \uacbd\ud5d8\ud560 \uc218 \uc788\ub3c4\ub85d \ucc57\ubd07 \ub370\ubaa8\uc640 \uad00\ub9ac \uc778\ud130\ud398\uc774\uc2a4 \ub370\ubaa8\ub97c \ud3ec\ud568\ud55c \uc5ec\ub7ec \uc628\ub77c\uc778 \ub370\ubaa8 \uc0ac\uc774\ud2b8\ub97c \uc81c\uacf5\ud569\ub2c8\ub2e4.\\n\\n- \ucc57\ubd07 \ub370\ubaa8: [https://demo.casibase.com](https://demo.casibase.com/)\\n- \uad00\ub9ac \uc778\ud130\ud398\uc774\uc2a4 \ub370\ubaa8: [https://demo-admin.casibase.com](https://demo-admin.casibase.com/)\\n\\n## Casibase\uc640 DeepSeek \ubaa8\ub378\uc758 \ud1b5\ud569:\\n\\nCasibase\ub97c \uad6c\ucd95\ud558\uace0 \uba87 \uac00\uc9c0 \uac04\ub2e8\ud55c \uc124\uc815\uc744 \ud1b5\ud574 DeepSeek R1, DeepSeek V3 \ubc0f Alibaba Cloud \ub4f1\uc758 \ud50c\ub7ab\ud3fc\uc5d0\uc11c \uc9c0\uc6d0\ud558\ub294 DeepSeek\uc758 \ub2e4\ub978 \ubc84\uc804 \ubc0f \uac15\ud654 \ubc84\uc804\uacfc \ub300\ud654\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub294 \ub85c\uceec \ubc30\ud3ec\uc758 \ubcf5\uc7a1\ud55c \uc124\uc815\uc744 \ud53c\ud560 \uc218 \uc788\uac8c \ud574\uc90d\ub2c8\ub2e4.\\n\\n### \ub2e8\uacc4 1: Casibase \ud658\uacbd \ubc30\ud3ec\\n\\n#### 1.1 **Deployment of Casdoor**.\\n\\nSince the powerful enterprise SSO authentication, authentication and other functions provided by Casibase are done based on Casdoor. Therefore, installing Casdoor is a prerequisite for using Casibase.\\n\\n_**Casdoor is a robust authentication system that provides a safe and secure login experience.**_\\n\\nYou can refer to the official Casdoor documentation to complete the Casdoor environment deployment\uff1a[Deployment of Casdoor](https://casdoor.org/docs/basic/server-installation/)\\n\\n#### 1.2 **Deployment of Casibase**: [Deployment of Casibase](https://casibase.org/zh/docs/basic/server-installation)\\n\\n### Step 2: Casibase Basic Configuration\\n\\n#### 2.1 **Configuring Casdoor**:\\n\\nYou need to complete three steps in the deployed Casdoor to support the presence of users accessing Casibase, i.e., Configure Organizations->Configure Apply under Authentication->Configure user. do the following:\\n\\n##### 2.1.1 Add Organisation\\n\\nConfiguring Organisations in the Casdoor website.\\n\\n![addOrganisations](/img/blog/casibase-deepSeek-guideline/add-organisations.png)\\n\\n##### 2.1.2 Add Apply\\n\\nSet \\"Apply\\" for Casibase. you should create it based on the Organizations you have configured, which will be reflected in the add form.\\n\\n![addApply](/img/blog/casibase-deepSeek-guideline/add-apply.png)\\n\\n![addApplications](/img/blog/casibase-deepSeek-guideline/add-applications.png)\\n\\n##### 2.1.3 Add User\\n\\nCreating a user for the created Apply is the final preparation for subsequent login to Casibase. Just follow the information on the form.\\n\\n![addUser](/img/blog/casibase-deepSeek-guideline/add-user.png)\\n\\n### 2.2 **Configuring Casibase**\uff1a\\n\\nWith the above configuration, we already have access to casibase, next, after logging in, we will configure it for AI chat, deploying a DeepSeek r1 based on the AlibabaCloud Big Model Service Platform (https://help.aliyun.com/zh/model-studio/) supported model.\\n\\n##### 2.2.1 Configure Storage Provider\\n\\nFirstly, it is necessary to **configure a storage provider** through Casdoor, which can be used to store data by adding this storage provider. They can be added to Casdoor by clicking the button on the homepage\uff1a`Authentication-> Providers->add`\\n\\n##### 2.2.2 Configure Model Provider\\n\\nIn Casibase, configure the new model provider: `Click the Providers button in the navigation bar at the top of the home page->add`.\\n\\n_**In the pop-up add form, we can set Category as Model, and then set the first level model: such as Moonshot, DeepSeek, Alibaba Cloud, etc. Here we don\'t directly select the AI model support provided by DeepSeek, but use the DeepSeek provided by Alibaba Cloud support, as shown below, we can see various sub-models supported by Alibaba Cloud in Sub Type.**_\\n\\n![addModelProvider](/img/blog/casibase-deepSeek-guideline/add-model-provider.png)\\n\\n##### 2.2.3 Configure Embedding Providers (Optional)\\n\\nAfter 2.2.1 and 2.2.2 have been added successfully, we can Configure Embedding Providers, by configuring Embedding Providers, we can integrate with our powerful knowledge base system Casibase. Similar steps to 2.2.2, fill out the form via: `Authentication-> Providers->add` (but please note that the Category needs to be configured as Embedding):\\n\\n![casibaseEmbedding](/img/blog/casibase-deepSeek-guideline/casibase-embedding.png)\\n\\n##### 2.2.4 Add stores.\\n\\nIn this step we organise all the information we added above (Storage provider, Model provider, Embedding provider), in the form we can fill in these three items and finally create a Stores.\\n\\n### Step 3: Chatting using the DeepSeek model based on Casibase and Alibaba Cloud\\n\\n![Chat](/img/blog/casibase-deepSeek-guideline/chat.png)\\n\\n## Conclusions\\n\\nIn this guide, we learned how to implement AI chat functionality in Casibase using DeepSeek. Now you can chat with chatbots in Casibase.\\n\\nFor more details, you can refer to [Casibase Docs](https://casibase.org/zh/docs/overview)."},{"id":"/welcome","metadata":{"permalink":"/ko/blog/welcome","editUrl":"https://github.com/casibase/casibase-website/edit/master/i18n/ko/docusaurus-plugin-content-blog/welcome.md","source":"@site/i18n/ko/docusaurus-plugin-content-blog/welcome.md","title":"\ud658\uc601\ud569\ub2c8\ub2e4","description":"Casibase \uc6f9\uc0ac\uc774\ud2b8\uc5d0 \uc624\uc2e0 \uac83\uc744 \ud658\uc601\ud569\ub2c8\ub2e4!","date":"2025-03-17T11:54:13.432Z","formattedDate":"2025\ub144 3\uc6d4 17\uc77c","tags":[{"label":"casibase","permalink":"/ko/blog/tags/casibase"}],"readingTime":0.075,"hasTruncateMarker":false,"authors":[{"name":"Casibase \ud300","url":"https://github.com/casibase","imageURL":"https://avatars.githubusercontent.com/casibase"}],"frontMatter":{"title":"\ud658\uc601\ud569\ub2c8\ub2e4","author":"Casibase \ud300","author_url":"https://github.com/casibase","author_image_url":"https://avatars.githubusercontent.com/casibase","tags":["casibase"]},"prevItem":{"title":"Casibase + DeepSeek \ud1b5\ud569 \uac00\uc774\ub4dc","permalink":"/ko/blog/casibase-deepSeek-guideline"}},"content":"Casibase \uc6f9\uc0ac\uc774\ud2b8\uc5d0 \uc624\uc2e0 \uac83\uc744 \ud658\uc601\ud569\ub2c8\ub2e4!"}]}')}}]);