"use strict";(self.webpackChunkcasibase_website=self.webpackChunkcasibase_website||[]).push([[8122],{2348:e=>{e.exports=JSON.parse('{"blogPosts":[{"id":"/casibase-deepSeek-guideline","metadata":{"permalink":"/ko/blog/casibase-deepSeek-guideline","editUrl":"https://github.com/casibase/casibase-website/edit/master/i18n/ko/docusaurus-plugin-content-blog/casibase-deepSeek-guideline.md","source":"@site/i18n/ko/docusaurus-plugin-content-blog/casibase-deepSeek-guideline.md","title":"Casibase + DeepSeek \ud1b5\ud569 \uac00\uc774\ub4dc","description":"\uc624\ub298\ub0a0\uc758 \ub514\uc9c0\ud138 \uc2dc\ub300\uc5d0\uc11c \uae30\uc5c5\uacfc \uac1c\uc778\uc758 \ud6a8\uc728\uc801\uc778 \uc9c0\uc2dd \uad00\ub9ac \uc2dc\uc2a4\ud15c\uacfc \uc9c0\ub2a5\ud615 \uc9c8\uc758\uc751\ub2f5 \uc2dc\uc2a4\ud15c\uc5d0 \ub300\ud55c \uc218\uc694\uac00 \uc99d\uac00\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc778\uacf5\uc9c0\ub2a5 \uae30\uc220\uc758 \uae09\uc18d\ud55c \ubc1c\uc804\uacfc \ud568\uaed8, \uc0ac\ub78c\ub4e4\uc740 \uc815\ubcf4 \ucc98\ub9ac\ub97c \uac1c\uc120\ud558\uace0, \uc6cc\ud06c\ud50c\ub85c\uc6b0\ub97c \ucd5c\uc801\ud654\ud558\uba70, \uc0ac\uc6a9\uc790 \uacbd\ud5d8\uc744 \ud5a5\uc0c1\uc2dc\ud0a4\uae30 \uc704\ud55c \ub354 \uc2a4\ub9c8\ud2b8\ud558\uace0 \ud6a8\uc728\uc801\uc778 \uc194\ub8e8\uc158\uc744 \ucc3e\uace0 \uc788\uc2b5\ub2c8\ub2e4.","date":"2025-10-14T11:29:34.735Z","formattedDate":"2025\ub144 10\uc6d4 14\uc77c","tags":[{"label":"Casibase","permalink":"/ko/blog/tags/casibase"}],"readingTime":9.38,"hasTruncateMarker":false,"authors":[{"name":"Casibase \ud300","url":"https://github.com/casibase","imageURL":"https://avatars.githubusercontent.com/casibase"}],"frontMatter":{"title":"Casibase + DeepSeek \ud1b5\ud569 \uac00\uc774\ub4dc","author":"Casibase \ud300","author_url":"https://github.com/casibase","author_image_url":"https://avatars.githubusercontent.com/casibase","tags":["Casibase"]},"nextItem":{"title":"\ud658\uc601\ud569\ub2c8\ub2e4","permalink":"/ko/blog/welcome"}},"content":"\uc624\ub298\ub0a0\uc758 \ub514\uc9c0\ud138 \uc2dc\ub300\uc5d0\uc11c \uae30\uc5c5\uacfc \uac1c\uc778\uc758 \ud6a8\uc728\uc801\uc778 \uc9c0\uc2dd \uad00\ub9ac \uc2dc\uc2a4\ud15c\uacfc \uc9c0\ub2a5\ud615 \uc9c8\uc758\uc751\ub2f5 \uc2dc\uc2a4\ud15c\uc5d0 \ub300\ud55c \uc218\uc694\uac00 \uc99d\uac00\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc778\uacf5\uc9c0\ub2a5 \uae30\uc220\uc758 \uae09\uc18d\ud55c \ubc1c\uc804\uacfc \ud568\uaed8, \uc0ac\ub78c\ub4e4\uc740 \uc815\ubcf4 \ucc98\ub9ac\ub97c \uac1c\uc120\ud558\uace0, \uc6cc\ud06c\ud50c\ub85c\uc6b0\ub97c \ucd5c\uc801\ud654\ud558\uba70, \uc0ac\uc6a9\uc790 \uacbd\ud5d8\uc744 \ud5a5\uc0c1\uc2dc\ud0a4\uae30 \uc704\ud55c \ub354 \uc2a4\ub9c8\ud2b8\ud558\uace0 \ud6a8\uc728\uc801\uc778 \uc194\ub8e8\uc158\uc744 \ucc3e\uace0 \uc788\uc2b5\ub2c8\ub2e4.\\n\\nCasibase\ub294 \uac15\ub825\ud55c \uba40\ud2f0 \ubaa8\ub378 \ud638\ud658\uc131, \uc5d4\ud130\ud504\ub77c\uc774\uc988\uae09 \uae30\ub2a5 \uc9c0\uc6d0, \uc9c1\uad00\uc801\uc778 \uc6f9 \uc778\ud130\ud398\uc774\uc2a4\ub97c \uac16\ucd98 \uc624\ud508\uc18c\uc2a4 AI \uc9c0\uc2dd\ubca0\uc774\uc2a4 \ubc0f \ub300\ud654 \uc2dc\uc2a4\ud15c\uc73c\ub85c\uc11c \ub9ce\uc740 \uac1c\ubc1c\uc790\uc640 \uae30\uc5c5\uc758 \uc120\ud638\ud558\ub294 \uc194\ub8e8\uc158\uc774 \ub418\uc5c8\uc2b5\ub2c8\ub2e4. \uc9c0\uc2dd\uc744 \ud6a8\uc728\uc801\uc73c\ub85c \uc870\uc9c1\ud558\uace0 \uac80\uc0c9\ud560 \uc218 \uc788\uc744 \ubfd0\ub9cc \uc544\ub2c8\ub77c, \ub2e4\uc591\ud55c \uc2dc\ub098\ub9ac\uc624\ub97c \uc9c0\uc6d0\ud558\uae30 \uc704\ud574 \ub2e4\uc591\ud55c AI \ubaa8\ub378\uc744 \uc27d\uac8c \ud1b5\ud569\ud560 \uc218 \uc788\ub294 \uc720\uc5f0\ud55c \uc778\ud130\ud398\uc774\uc2a4\ub97c \uc81c\uacf5\ud569\ub2c8\ub2e4.\\n\\n\ub3d9\uc2dc\uc5d0, \ucd5c\uadfc \ucd9c\uc2dc\ub41c \uace0\uae09 AI \ubaa8\ub378\uc778 **DeepSeek R1**\uc740 \ub6f0\uc5b4\ub09c \uc131\ub2a5, \ucd5c\uc801\ud654\ub41c \uc99d\ub958 \uae30\uc220, \ubb34\ub8cc \uc624\ud508\uc18c\uc2a4 \ud2b9\uc131\uc73c\ub85c \uc778\ud574 \uac1c\ubc1c\uc790 \ucee4\ubba4\ub2c8\ud2f0\uc5d0\uc11c \ud070 \uc8fc\ubaa9\uc744 \ubc1b\uc558\uc2b5\ub2c8\ub2e4. DeepSeek R1\uc758 \uac15\ub825\ud55c \ucd94\ub860 \ub2a5\ub825\uacfc \ud6a8\uc728\uc801\uc778 \uc9c0\uc2dd \ud1b5\ud569 \ub2a5\ub825\uc740 \uc9c0\ub2a5\ud615 \uc9c8\uc758\uc751\ub2f5, \ucf54\ub4dc \uc0dd\uc131, \ud14d\uc2a4\ud2b8 \uc774\ud574 \ub4f1\uc758 \uc791\uc5c5\uc5d0\uc11c \ub6f0\uc5b4\ub09c \uc131\uacfc\ub97c \ubcf4\uc774\uba70 \uc624\ud508\uc18c\uc2a4 AI \uc0dd\ud0dc\uacc4\uc758 \uc911\uc694\ud55c \uad6c\uc131\uc6d0\uc774 \ub418\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uc774 \uae00\uc5d0\uc11c\ub294 Casibase\ub97c DeepSeek R1\uacfc \ud6a8\uc728\uc801\uc73c\ub85c \ud1b5\ud569\ud558\uc5ec \uace0\uc131\ub2a5, \ubcf4\uc548\uc131, \uac15\ub825\ud55c \uae30\ub2a5\uc744 \uac16\ucd98 AI \uc9c0\uc2dd \uc2dc\uc2a4\ud15c\uacfc \ub300\ud654 \uc2dc\uc2a4\ud15c\uc744 \uad6c\ucd95\ud558\ub294 \ubc29\ubc95\uc744 \ub17c\uc758\ud560 \uac83\uc785\ub2c8\ub2e4. \ud658\uacbd \uc124\uc815\ubd80\ud130 \uc2e4\uc81c \uc751\uc6a9\uae4c\uc9c0\uc758 \ud575\uc2ec \ub2e8\uacc4\ub97c \uc18c\uac1c\ud558\uace0, Casibase\uc758 \uc5d4\ud130\ud504\ub77c\uc774\uc988\uae09 \uae30\ub2a5\uacfc DeepSeek R1\uc758 \uac15\ub825\ud55c \ub2a5\ub825\uc744 \ud65c\uc6a9\ud558\uc5ec \uc815\ud655\ud558\uace0 \ud6a8\uc728\uc801\uc778 \uc9c0\ub2a5\ud615 \uc9c8\uc758\uc751\ub2f5\uacfc \uc9c0\uc2dd \uad00\ub9ac\ub97c \uc2e4\ud604\ud558\ub294 \ubc29\ubc95\uc744 \ubd84\uc11d\ud560 \uac83\uc785\ub2c8\ub2e4.\\n\\n## Casibase\ub780:\\n\\nCasibase\ub294 \ucd5c\uc2e0 RAG(Retrieval-Augmented Generation) \uae30\uc220, \uc5d4\ud130\ud504\ub77c\uc774\uc988\uae09 \uc2f1\uae00 \uc0ac\uc778\uc628(SSO), \uc8fc\ub958 AI \ubaa8\ub378\uc5d0 \ub300\ud55c \uad11\ubc94\uc704\ud55c \uc9c0\uc6d0\uc744 \uacb0\ud569\ud55c \uc624\ud508\uc18c\uc2a4 AI \uc9c0\uc2dd \uc2dc\uc2a4\ud15c \ubc0f \ub300\ud654 \uc2dc\uc2a4\ud15c\uc785\ub2c8\ub2e4. LangChain\uacfc \uc720\uc0ac\ud55c \uc2dc\uc2a4\ud15c\uc73c\ub85c\uc11c, Casibase\ub294 \uae30\uc5c5\uacfc \uac1c\ubc1c\uc790\uc5d0\uac8c \uac15\ub825\ud558\uace0 \uc720\uc5f0\ud558\uba70 \uc0ac\uc6a9\ud558\uae30 \uc26c\uc6b4 \uc9c0\uc2dd \uad00\ub9ac \ubc0f \uc9c0\ub2a5\ud615 \ub300\ud654 \ud50c\ub7ab\ud3fc\uc744 \uc81c\uacf5\ud558\ub294 \uac83\uc744 \ubaa9\ud45c\ub85c \ud569\ub2c8\ub2e4.\\n\\n\ud604\uc7ac Casibase\ub294 OpenAI \uae30\ubc18 GPT \uc2dc\ub9ac\uc988, Gemini, Claude, Moonshot, DeepSeek \ub4f1\uc758 \uc5b8\uc5b4 \ubaa8\ub378\uacfc OpenAI \uae30\ubc18 \uc784\ubca0\ub529 \ubaa8\ub378, Hugging Face\uc758 sentence-transformers, Cohere, Qwen \ub4f1\uc758 \uc784\ubca0\ub529 \ubaa8\ub378\uc744 \uc9c0\uc6d0\ud569\ub2c8\ub2e4. Hugging Face, Cohere, Qwen \ubc0f \uae30\ud0c0 \uc784\ubca0\ub529 \ubaa8\ub378. \uc774\ub7ec\ud55c \uad11\ubc94\uc704\ud55c \ubaa8\ub378 \uc9c0\uc6d0\uc744 \ud1b5\ud574 \uc0ac\uc6a9\uc790\ub294 \uc694\uad6c\uc0ac\ud56d\uacfc \uc120\ud638\ub3c4\uc5d0 \ub530\ub77c \uac00\uc7a5 \uc801\ud569\ud55c AI \ubaa8\ub378\uc744 \uc120\ud0dd\ud560 \uc218 \uc788\uc73c\uba70, \ud5a5\ud6c4 \uc0c8\ub85c\uc6b4 \ubaa8\ub378\uc758 \ud1b5\ud569\ub3c4 \uc6a9\uc774\ud569\ub2c8\ub2e4.\\n\\n### Casibase \uc628\ub77c\uc778 \ub370\ubaa8:\\n\\nCasibase\ub294 \uc0ac\uc6a9\uc790\uac00 \uc2dc\uc2a4\ud15c \uae30\ub2a5\uc744 \uc9c1\uad00\uc801\uc73c\ub85c \uacbd\ud5d8\ud560 \uc218 \uc788\ub3c4\ub85d \ucc57\ubd07 \ub370\ubaa8\uc640 \uad00\ub9ac \uc778\ud130\ud398\uc774\uc2a4 \ub370\ubaa8\ub97c \ud3ec\ud568\ud55c \uc5ec\ub7ec \uc628\ub77c\uc778 \ub370\ubaa8 \uc0ac\uc774\ud2b8\ub97c \uc81c\uacf5\ud569\ub2c8\ub2e4.\\n\\n- \ucc57\ubd07 \ub370\ubaa8: [https://demo.casibase.com](https://demo.casibase.com/)\\n- \uad00\ub9ac \uc778\ud130\ud398\uc774\uc2a4 \ub370\ubaa8: [https://demo-admin.casibase.com](https://demo-admin.casibase.com/)\\n\\n## Casibase\uc640 DeepSeek \ubaa8\ub378\uc758 \ud1b5\ud569:\\n\\nCasibase\ub97c \uad6c\ucd95\ud558\uace0 \uba87 \uac00\uc9c0 \uac04\ub2e8\ud55c \uc124\uc815\uc744 \ud1b5\ud574 DeepSeek R1, DeepSeek V3 \ubc0f Alibaba Cloud \ub4f1\uc758 \ud50c\ub7ab\ud3fc\uc5d0\uc11c \uc9c0\uc6d0\ud558\ub294 DeepSeek\uc758 \ub2e4\ub978 \ubc84\uc804 \ubc0f \uac15\ud654 \ubc84\uc804\uacfc \ub300\ud654\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub294 \ub85c\uceec \ubc30\ud3ec\uc758 \ubcf5\uc7a1\ud55c \uc124\uc815\uc744 \ud53c\ud560 \uc218 \uc788\uac8c \ud574\uc90d\ub2c8\ub2e4.\\n\\n### \ub2e8\uacc4 1: Casibase \ud658\uacbd \ubc30\ud3ec\\n\\n#### 1.1 **Casdoor \ubc30\ud3ec**.\\n\\nCasibase\uc5d0\uc11c \uc81c\uacf5\ud558\ub294 \uac15\ub825\ud55c \uae30\uc5c5\uc6a9 SSO \uc778\uc99d \ubc0f \uae30\ud0c0 \uae30\ub2a5\ub4e4\uc774 Casdoor\ub97c \uae30\ubc18\uc73c\ub85c \uc218\ud589\ub418\uae30 \ub54c\ubb38\uc785\ub2c8\ub2e4. \ub530\ub77c\uc11c Casibase\ub97c \uc0ac\uc6a9\ud558\uae30 \uc704\ud574\uc11c\ub294 Casdoor \uc124\uce58\uac00 \uc804\uc81c \uc870\uac74\uc785\ub2c8\ub2e4.\\n\\n_**Casdoor\ub294 \uc548\uc804\ud558\uace0 \uc2e0\ub8b0\ud560 \uc218 \uc788\ub294 \ub85c\uadf8\uc778 \uacbd\ud5d8\uc744 \uc81c\uacf5\ud558\ub294 \uac15\ub825\ud55c \uc778\uc99d \uc2dc\uc2a4\ud15c\uc785\ub2c8\ub2e4.**_\\n\\nCasdoor \ud658\uacbd \ubc30\ud3ec\ub97c \uc644\ub8cc\ud558\ub824\uba74 \uacf5\uc2dd Casdoor \ubb38\uc11c\ub97c \ucc38\uace0\ud558\uc138\uc694\uff1a[Casdoor \ubc30\ud3ec](https://casdoor.org/docs/basic/server-installation/)\\n\\n#### 1.2 **Casibase \ubc30\ud3ec**: [Casibase \ubc30\ud3ec](https://casibase.org/zh/docs/basic/server-installation)\\n\\n### \ub2e8\uacc4 <b>2</b>: Casibase \uae30\ubcf8 \uad6c\uc131\\n\\n#### 2.1 **Casdoor \uad6c\uc131**:\\n\\nCasibase\uc5d0 \uc811\uadfc\ud558\ub294 \uc0ac\uc6a9\uc790\ub97c \uc9c0\uc6d0\ud558\uae30 \uc704\ud574, \ubc30\ud3ec\ub41c Casdoor\uc5d0\uc11c \uc138 \uac00\uc9c0 \ub2e8\uacc4\ub97c \uc644\ub8cc\ud574\uc57c \ud569\ub2c8\ub2e4. \uc989, \uc870\uc9c1 \uad6c\uc131 -> \uc778\uc99d \ud558\uc758 \uc2e0\uccad \uad6c\uc131 -> \uc0ac\uc6a9\uc790 \uad6c\uc131\uc744 \uc9c4\ud589\ud574\uc57c \ud569\ub2c8\ub2e4. \ub2e4\uc74c \uc791\uc5c5\uc744 \uc218\ud589\ud558\uc138\uc694:\\n\\n##### 2.1.1 \uc870\uc9c1 \ucd94\uac00\\n\\nCasdoor \uc6f9\uc0ac\uc774\ud2b8\uc5d0\uc11c \uc870\uc9c1\uc744 \uad6c\uc131\ud569\ub2c8\ub2e4.\\n\\n![addOrganisations](/img/blog/casibase-deepSeek-guideline/add-organisations.png)\\n\\n##### 2.1.2 \uc2e0\uccad \ucd94\uac00\\n\\nCasibase\uc5d0 \ub300\ud574 \\"Apply\\"\ub97c \uc124\uc815\ud558\uc138\uc694. \uad6c\uc131\ud55c \uc870\uc9c1\uc744 \uae30\ubc18\uc73c\ub85c \uc0dd\uc131\ud574\uc57c \ud558\uba70, \uc774\ub294 \ucd94\uac00 \uc591\uc2dd\uc5d0 \ubc18\uc601\ub429\ub2c8\ub2e4.\\n\\n![addApply](/img/blog/casibase-deepSeek-guideline/add-apply.png)\\n\\n![addApplications](/img/blog/casibase-deepSeek-guideline/add-applications.png)\\n\\n##### 2.1.3 \uc0ac\uc6a9\uc790 \ucd94\uac00\\n\\n\uc0dd\uc131\ub41c \uc2e0\uccad\uc5d0 \ub300\ud574 \uc0ac\uc6a9\uc790\ub97c \uc0dd\uc131\ud558\ub294 \uac83\uc774 \uc774\ud6c4 Casibase \ub85c\uadf8\uc778 \uc900\ube44\uc758 \ub9c8\uc9c0\ub9c9 \ub2e8\uacc4\uc785\ub2c8\ub2e4. \uc591\uc2dd\uc758 \uc815\ubcf4\ub97c \uadf8\ub300\ub85c \ub530\ub974\uba74 \ub429\ub2c8\ub2e4.\\n\\n![addUser](/img/blog/casibase-deepSeek-guideline/add-user.png)\\n\\n### 2.2 **Casibase \uad6c\uc131**\uff1a\\n\\n\uc704\uc758 \uad6c\uc131\uc744 \ud1b5\ud574 \uc774\ubbf8 Casibase\uc5d0 \uc811\uadfc\ud560 \uc218 \uc788\uac8c \ub418\uc5c8\uc73c\uba70, \uc774\ud6c4 \ub85c\uadf8\uc778 \ud6c4 AI \ucc44\ud305 \uae30\ub2a5\uc744 \uc704\ud574 AlibabaCloud Big Model Service Platform(https://help.aliyun.com/zh/model-studio/)\uc5d0\uc11c \uc9c0\uc6d0\ud558\ub294 \ubaa8\ub378\uc744 \uae30\ubc18\uc73c\ub85c DeepSeek r1\uc744 \ubc30\ud3ec\ud560 \uac83\uc785\ub2c8\ub2e4.\\n\\n##### 2.2.1 \uc2a4\ud1a0\ub9ac\uc9c0 \uacf5\uae09\uc790 \uad6c\uc131\\n\\n\uba3c\uc800, Casdoor\ub97c \ud1b5\ud574 \ub370\uc774\ud130\ub97c \uc800\uc7a5\ud558\uae30 \uc704\ud55c **\uc2a4\ud1a0\ub9ac\uc9c0 \uacf5\uae09\uc790**\ub97c \uad6c\uc131\ud574\uc57c \ud569\ub2c8\ub2e4. \ud648\ud398\uc774\uc9c0\uc5d0\uc11c \ubc84\ud2bc\uc744 \ud074\ub9ad\ud558\uc5ec Casdoor\uc5d0 \ucd94\uac00\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4\uff1a`Authentication-> Providers->add`\\n\\n##### 2.2.2 \ubaa8\ub378 \uacf5\uae09\uc790 \uad6c\uc131\\n\\nCasibase\uc5d0\uc11c \uc0c8\ub85c\uc6b4 \ubaa8\ub378 \uacf5\uae09\uc790\ub97c \uad6c\uc131\ud569\ub2c8\ub2e4: `\ud648\ud398\uc774\uc9c0 \uc0c1\ub2e8 \ub124\ube44\uac8c\uc774\uc158 \ubc14\uc5d0\uc11c Providers \ubc84\ud2bc\uc744 \ud074\ub9ad -> add`.\\n\\n_**\ud31d\uc5c5 \ucd94\uac00 \uc591\uc2dd\uc5d0\uc11c Category\ub97c Model\ub85c \uc124\uc815\ud55c \ud6c4, \uccab \ubc88\uc9f8 \uc218\uc900 \ubaa8\ub378, \uc608: Moonshot, DeepSeek, Alibaba Cloud \ub4f1\uc744 \uc124\uc815\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.**_. \uc5ec\uae30\uc5d0\uc11c\ub294 DeepSeek\uc5d0\uc11c \uc9c1\uc811 \uc81c\uacf5\ud558\ub294 AI \ubaa8\ub378 \uc9c0\uc6d0\uc744 \uc120\ud0dd\ud558\uc9c0 \uc54a\uace0, Alibaba Cloud\uac00 \uc81c\uacf5\ud558\ub294 DeepSeek \uc9c0\uc6d0\uc744 \uc0ac\uc6a9\ud569\ub2c8\ub2e4. \uc544\ub798\uc5d0\uc11c \ubcf4\uc2dc\ub2e4\uc2dc\ud53c Sub Type\uc5d0\uc11c Alibaba Cloud\uac00 \uc9c0\uc6d0\ud558\ub294 \ub2e4\uc591\ud55c \ud558\uc704 \ubaa8\ub378\ub4e4\uc744 \ud655\uc778\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\\\*\\\\*\\\\*\\n\\n![addModelProvider](/img/blog/casibase-deepSeek-guideline/add-model-provider.png)\\n\\n##### 2.2.3 \uc784\ubca0\ub529 \uacf5\uae09\uc790 \uad6c\uc131 (\uc120\ud0dd \uc0ac\ud56d)\\n\\n2.2.1\uacfc 2.2.2\uac00 \uc131\uacf5\uc801\uc73c\ub85c \ucd94\uac00\ub41c \ud6c4, \uc784\ubca0\ub529 \uacf5\uae09\uc790\ub97c \uad6c\uc131\ud560 \uc218 \uc788\uc73c\uba70, \uc774\ub97c \ud1b5\ud574 \uac15\ub825\ud55c \uc9c0\uc2dd \uae30\ubc18 \uc2dc\uc2a4\ud15c Casibase\uc640 \ud1b5\ud569\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. 2.2.2\uc640 \uc720\uc0ac\ud55c \ub2e8\uacc4\ub85c, `Authentication-> Providers->add`\ub97c \ud1b5\ud574 \uc591\uc2dd\uc744 \uc791\uc131\ud558\uc138\uc694 (\ub2e8, Category\ub294 Embedding\uc73c\ub85c \uad6c\uc131\ub418\uc5b4\uc57c \ud569\ub2c8\ub2e4):\\n\\n![casibaseEmbedding](/img/blog/casibase-deepSeek-guideline/casibase-embedding.png)\\n\\n##### 2.2.4 \uc2a4\ud1a0\uc5b4 \ucd94\uac00.\\n\\n\uc774 \ub2e8\uacc4\uc5d0\uc11c\ub294 \uc704\uc5d0\uc11c \ucd94\uac00\ud55c \ubaa8\ub4e0 \uc815\ubcf4\ub97c \uc815\ub9ac\ud569\ub2c8\ub2e4 (\uc2a4\ud1a0\ub9ac\uc9c0 \uacf5\uae09\uc790, \ubaa8\ub378 \uacf5\uae09\uc790, \uc784\ubca0\ub529 \uacf5\uae09\uc790). \ud574\ub2f9 \uc591\uc2dd\uc5d0 \uc774 \uc138 \uac00\uc9c0 \ud56d\ubaa9\uc744 \uc791\uc131\ud55c \ud6c4 \ucd5c\uc885\uc801\uc73c\ub85c \uc2a4\ud1a0\uc5b4\ub97c \uc0dd\uc131\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n### \ub2e8\uacc4 <b>3</b>: Casibase\uc640 Alibaba Cloud\ub97c \uae30\ubc18\uc73c\ub85c \ud55c DeepSeek \ubaa8\ub378\uc744 \uc0ac\uc6a9\ud55c \ucc44\ud305\\n\\n![Chat](/img/blog/casibase-deepSeek-guideline/chat.png)\\n\\n## \uacb0\ub860\\n\\n\uc774 \uac00\uc774\ub4dc\uc5d0\uc11c\ub294 DeepSeek\ub97c \uc0ac\uc6a9\ud558\uc5ec Casibase\uc5d0\uc11c AI \ucc44\ud305 \uae30\ub2a5\uc744 \uad6c\ud604\ud558\ub294 \ubc29\ubc95\uc744 \ubc30\uc6e0\uc2b5\ub2c8\ub2e4. \uc774\uc81c Casibase\uc5d0\uc11c \ucc57\ubd07\uacfc \ub300\ud654\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\uc790\uc138\ud55c \ub0b4\uc6a9\uc740 [Casibase Docs](https://casibase.org/zh/docs/overview)\ub97c \ucc38\uc870\ud558\uc138\uc694."},{"id":"/welcome","metadata":{"permalink":"/ko/blog/welcome","editUrl":"https://github.com/casibase/casibase-website/edit/master/i18n/ko/docusaurus-plugin-content-blog/welcome.md","source":"@site/i18n/ko/docusaurus-plugin-content-blog/welcome.md","title":"\ud658\uc601\ud569\ub2c8\ub2e4","description":"Casibase \uc6f9\uc0ac\uc774\ud2b8\uc5d0 \uc624\uc2e0 \uac83\uc744 \ud658\uc601\ud569\ub2c8\ub2e4!","date":"2025-10-14T11:29:34.735Z","formattedDate":"2025\ub144 10\uc6d4 14\uc77c","tags":[{"label":"casibase","permalink":"/ko/blog/tags/casibase"}],"readingTime":0.075,"hasTruncateMarker":false,"authors":[{"name":"Casibase \ud300","url":"https://github.com/casibase","imageURL":"https://avatars.githubusercontent.com/casibase"}],"frontMatter":{"title":"\ud658\uc601\ud569\ub2c8\ub2e4","author":"Casibase \ud300","author_url":"https://github.com/casibase","author_image_url":"https://avatars.githubusercontent.com/casibase","tags":["casibase"]},"prevItem":{"title":"Casibase + DeepSeek \ud1b5\ud569 \uac00\uc774\ub4dc","permalink":"/ko/blog/casibase-deepSeek-guideline"}},"content":"Casibase \uc6f9\uc0ac\uc774\ud2b8\uc5d0 \uc624\uc2e0 \uac83\uc744 \ud658\uc601\ud569\ub2c8\ub2e4!"}]}')}}]);